import{_ as e}from"./plugin-vue_export-helper-DlAUqK2U.js";import{c as n,e as p,o as s}from"./app-DOgujuAr.js";const r={};function o(c,a){return s(),n("div",null,a[0]||(a[0]=[p('<h1 id="包管理器-npm" tabindex="-1"><a class="header-anchor" href="#包管理器-npm"><span>包管理器 NPM</span></a></h1><blockquote><p>[!help] 参考资料</p><p><a href="">教程 - </a></p></blockquote><h1 id="扩展知识" tabindex="-1"><a class="header-anchor" href="#扩展知识"><span>扩展知识</span></a></h1><h1 id="面试问答" tabindex="-1"><a class="header-anchor" href="#面试问答"><span>面试问答</span></a></h1><h2 id="npm、pnpm、yarn-之间的区别" tabindex="-1"><a class="header-anchor" href="#npm、pnpm、yarn-之间的区别"><span>NPM、PNPM、Yarn 之间的区别？</span></a></h2><p>npm 是 Nodejs 官方指定的包管理，最大的优点就是有官方的背书，直接下载一个 Nodejs 就可以使用了，下载的模块也一定是最全面最权威的。他有一个很致命的问题，那就是：对依赖的管理不合理，不合理之处总结有二：1、依赖之间嵌套严重，导致项目中 <code>node_modules</code> 过于庞大，非常占用空间；2、版本不一致导致的开发环境的不一致，虽然都是同一个 package.json 文件，但不同的环境下载的依赖却会出现不同。</p><p>而 yarn 和 pnpm 则是为了解决 npm 所遗留下的问题而诞生的，yarn 是 Facebook 开发的，有着更好的控制管理，采用并行的方式来让下载速度加快。pnpm 则是一个更新的包管理工具了，通过硬链接和符号链接的方式复用已下载的包，大幅减少 <code>node_modules</code> 的体积，同样使用并行操作来加强性能，且采用了更严格的依赖管理。</p><h2 id="关于-cnpm" tabindex="-1"><a class="header-anchor" href="#关于-cnpm"><span>关于 CNPM ？</span></a></h2><p>关于 <code>cnpm</code> ：和 npm 的功能基本一致，但其仓库源来自于国内的淘宝团队，这样可以解决 npm 在国内使用的网络问题；需要注意的是淘宝与 npm 官方的同步频率为 10min 一次，所以有部分依赖可能不是最新的。目前，其实不是很推荐使用 cnpm ，因为你完全可以通过修改 npm 的镜像源来修改下载来源。</p><h2 id="关于最近的-bun" tabindex="-1"><a class="header-anchor" href="#关于最近的-bun"><span>关于最近的 Bun ？</span></a></h2><p>关于 <code>bun</code> ：这是一个和 Nodejs 竞争的框架 bun 的包管理工具，由于 bun 可以完全兼容 npm 的包，所以也可以用来下载前端的这些依赖。</p><blockquote><p>bun 当前在前端领域可以说非常的『新/稚嫩』，网上的参考教程也非常少，所以请谨慎使用（除非你的动手能力或者探索精神MAX）。</p></blockquote><h1 id="其他主流包管理器" tabindex="-1"><a class="header-anchor" href="#其他主流包管理器"><span>其他主流包管理器</span></a></h1><h2 id="pnpm" tabindex="-1"><a class="header-anchor" href="#pnpm"><span>PNPM</span></a></h2><h2 id="yarn" tabindex="-1"><a class="header-anchor" href="#yarn"><span>Yarn</span></a></h2><h2 id="bun" tabindex="-1"><a class="header-anchor" href="#bun"><span>Bun</span></a></h2>',16)]))}const t=e(r,[["render",o]]),m=JSON.parse('{"path":"/2_Chesses/%E8%AE%A1%E7%AE%97%E6%9C%BA%E9%A2%86%E5%9F%9F/WEB%E5%BC%80%E5%8F%91%EF%BC%9A%E5%89%8D%E7%AB%AF/%E5%8C%85%E7%AE%A1%E7%90%86%E5%99%A8NPM.html","title":"包管理器 NPM","lang":"zh-CN","frontmatter":{"category":"学习笔记","tags":["NPM","PNPM","Yarn"]},"git":{"createdTime":1738837943000,"updatedTime":1744556425000,"contributors":[{"name":"cafe","username":"cafe","email":"18320210361@163.com","commits":1,"url":"https://github.com/cafe"},{"name":"CoffeeKiller","username":"CoffeeKiller","email":"18320210361@163.com","commits":1,"url":"https://github.com/CoffeeKiller"}]},"readingTime":{"minutes":1.81,"words":543},"filePathRelative":"2_Chesses/计算机领域/WEB开发：前端/包管理器NPM.md"}');export{t as comp,m as data};
